import React, { useState } from 'react';
import axios from 'axios';
import QrScanner from 'react-qr-scanner'; // Import the QR scanner

const API_BASE_URL = 'http://192.168.137.161:5000'; // Replace with your backend URL

const RecyclePage = () => {
  const [formData, setFormData] = useState({
    itemName: '',
    category: '',
    weight: '',
    age: '',
    condition: '',
  });
  const [points, setPoints] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isQRCodeScanned, setIsQRCodeScanned] = useState(false); // Add state to track if QR code is scanned
  const [qrError, setQrError] = useState(null);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission (recycling process)
  const handleRecycle = async () => {
    const { itemName, category, weight, age, condition } = formData;

    // Validation checks
    if (!itemName || !category || !weight || !age || !condition) {
      alert('Please fill out all fields.');
      return;
    }
    if (weight <= 0) {
      alert('Weight must be a positive number.');
      return;
    }
    if (age < 0) {
      alert('Age cannot be negative.');
      return;
    }

    // Retrieve email from localStorage
    const userEmail = localStorage.getItem('email');
    if (!userEmail) {
      alert('User email not found. Please log in again.');
      return;
    }

    setIsAnalyzing(true);

    try {
      // Send the recycling data and email to the server
      const response = await axios.post(`${API_BASE_URL}/recyclepage`, {
        itemName,
        category,
        weight,
        age,
        condition,
        email: userEmail, // Ensure email is passed to the server
      });

      setPoints(response.data.points);
    } catch (error) {
      console.error('Error submitting recycling data:', error);
      alert('Error submitting data. Please try again.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  // Handle QR code scan
  const handleScan = (data) => {
    if (data) {
      if (data === 'https://www.linkinglearning.com.au/') { // Replace with your dummy QR code value
        setIsQRCodeScanned(true); // QR code is valid
        setQrError(null); // Reset any error
        console.log("")
      } else {
        setQrError('Invalid QR Code. Please scan the correct QR code.');
      }
    }
  };

  const handleError = (err) => {
    console.error(err);
    setQrError('Error scanning QR Code. Please try again.');
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8 flex flex-col items-center mt-20">
      <h1 className="text-3xl font-bold mb-8">Recycle Your E-Waste</h1>

      {/* QR Code Scanner */}
      {!isQRCodeScanned && (
        <div className="mb-8">
          <h2 className="text-xl mb-4">Scan QR Code to proceed</h2>
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            style={{ width: '300px' }}
          />
          {qrError && <p className="text-red-500 mt-2">{qrError}</p>}
        </div>
      )}

      {/* Show form only after QR code is scanned */}
      {isQRCodeScanned && (
        <>
          <div className="flex flex-col mb-4">
            <label htmlFor="itemName" className="mb-2">Item Name</label>
            <input
              type="text"
              id="itemName"
              name="itemName"
              value={formData.itemName}
              onChange={handleInputChange}
              className="p-2 rounded bg-gray-800 text-white"
              placeholder="Enter the item name"
            />
          </div>

          <div className="flex flex-col mb-4">
            <label htmlFor="category" className="mb-2">Category</label>
            <select
              id="category"
              name="category"
              value={formData.category}
              onChange={handleInputChange}
              className="p-2 rounded bg-gray-800 text-white"
            >
              <option value="" disabled>Select E-Waste Category</option>
              <option value="Smartphone">Smartphone</option>
              <option value="Laptop">Laptop</option>
              <option value="Tablet">Tablet</option>
              <option value="Other">Other</option>
            </select>
          </div>

          <div className="flex flex-col mb-4">
            <label htmlFor="weight" className="mb-2">Item Weight (in kg)</label>
            <input
              type="number"
              id="weight"
              name="weight"
              value={formData.weight}
              onChange={handleInputChange}
              className="p-2 rounded bg-gray-800 text-white"
              placeholder="Enter the item weight"
            />
          </div>

          <div className="flex flex-col mb-4">
            <label htmlFor="age" className="mb-2">Item Age (in years)</label>
            <input
              type="number"
              id="age"
              name="age"
              value={formData.age}
              onChange={handleInputChange}
              className="p-2 rounded bg-gray-800 text-white"
              placeholder="Enter how old the item is"
            />
          </div>

          <div className="flex flex-col mb-4">
            <label htmlFor="condition" className="mb-2">Condition</label>
            <select
              id="condition"
              name="condition"
              value={formData.condition}
              onChange={handleInputChange}
              className="p-2 rounded bg-gray-800 text-white"
            >
              <option value="" disabled>Select Condition</option>
              <option value="Good">Good</option>
              <option value="Moderate">Moderate</option>
              <option value="Poor">Poor</option>
            </select>
          </div>

          <button
            onClick={handleRecycle}
            className="bg-green-500 px-4 py-2 rounded hover:bg-green-600"
            disabled={!formData.itemName || !formData.category || !formData.weight || !formData.age || !formData.condition}
          >
            Recycle Now
          </button>

          {isAnalyzing && <p className="mt-4">Analyzing your e-waste...</p>}
          {points && (
            <div className="mt-4">
              <h3 className="text-xl">You've earned {points} points!</h3>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default RecyclePage;
