import React, { useState } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://192.168.1.10:5000'; // Replace with your backend URL

const RecyclePage = () => {
  const [category, setCategory] = useState('');
  const [points, setPoints] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const handleRecycle = async () => {
    if (!category) {
      alert('Please select a category');
      return;
    }

    setIsAnalyzing(true); // Show analyzing process

    try {
      const response = await axios.post(`${API_BASE_URL}/recycle`, { category });

      setPoints(response.data.points); // Get rewarded points from the server
    } catch (error) {
      console.error('Error submitting recycling data:', error);
    } finally {
      setIsAnalyzing(false); // Stop analyzing process
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8 flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-8 ">Recycle Your E-Waste</h1>

      <select
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        className="mb-4 p-2 rounded bg-gray-800 text-white"
      >
        <option value="" disabled>Select E-Waste Category</option>
        <option value="Smartphone">Smartphone</option>
        <option value="Laptop">Laptop</option>
        <option value="Tablet">Tablet</option>
      </select>

      <button
        onClick={handleRecycle}
        className="bg-green-500 px-4 py-2 rounded hover:bg-green-600"
      >
        Recycle Now
      </button>

      {isAnalyzing && <p className="mt-4">Analyzing your e-waste...</p>}
      {points && (
        <div className="mt-4">
          <h3 className="text-xl">You've earned {points} points!</h3>
        </div>
      )}
    </div>
  );
};

export default RecyclePage;
