import React, { useState } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://192.168.137.161:5000'; // Replace with your backend URL

const RecyclePage = () => {
  const [formData, setFormData] = useState({
    itemName: '',
    category: '',
    weight: '',
    age: '',
    condition: '',
  });
  const [points, setPoints] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleRecycle = async () => {
    const { itemName, category, weight, age, condition } = formData;

    // Validation checks
    if (!itemName || !category || !weight || !age || !condition) {
      alert('Please fill out all fields.');
      return;
    }

    // Retrieve email from localStorage
    const userEmail = localStorage.getItem('email');
    if (!userEmail) {
      alert('User email not found. Please log in again.');
      return;
    }

    setIsAnalyzing(true); // Show analyzing process

    try {
      const response = await axios.post(`${API_BASE_URL}/recyclepage`, {
        itemName,
        category,
        weight,
        age,
        condition,
        email: userEmail // Pass the user's email to the server
      });

      setPoints(response.data.points); // Get rewarded points from the server
    } catch (error) {
      console.error('Error submitting recycling data:', error);
      alert('Error submitting data. Please try again.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div>
      {/* Recycling form */}
      <button onClick={handleRecycle}>Recycle Now</button>
    </div>
  );
};

export default RecyclePage;
